import React, { useState } from "react";
import "../App.css";
import { search } from '../BooksAPI';

export const SearchBook = ({ showSearchPage, setShowSearchpage, shelves, setShelves }) => {
  const [searchedBooks, setSearchedBooks] = useState([]);
  const onSearch = async (event) => {
    if (event.key === 'Enter') {
      let response = await search(event.target.value, 50);
      let _searchedBooks = response.map((res) => { 
        return { authors: res.authors, id: res.id, title: res.title, image: res.imageLinks.thumbnail } 
      });
      debugger;
      setSearchedBooks(_searchedBooks)
    }
  }

  const handleOnSelect = (bookId) => {
    debugger;
  }

  return (
  <div className="search-books">
    <div className="search-books-bar">
      <a
        className="close-search"
        onClick={() => setShowSearchpage(!showSearchPage)}
      >
        Close
      </a>
      <div className="search-books-input-wrapper">
        <input
          type="text"
          placeholder="Search by title, author, or ISBN"
          onKeyDown={onSearch}
        />
      </div>
    </div>
    <div className="search-books-results">
      <ol className="books-grid">
        {searchedBooks.map((book, index) => {
          return <div key={index} className="book">
            <div className="book-top">
              <div
                className="book-cover"
                style={{
                  width: 128,
                  height: 193,
                  backgroundImage: `url(${book.image})`
                }}
              ></div>
              <div className="book-shelf-changer">
                <select onChange={() => handleOnSelect(book.id)}>
                  <option value="none" disabled>
                    Add to...
                  </option>
                  <option value="currentlyReading">
                    Currently Reading
                  </option>
                  <option value="wantToRead">Want to Read</option>
                  <option value="read">Read</option>
                </select>
              </div>
            </div>
            <div className="book-title">{book.title}</div>
            <div className="book-authors">{book.authors?.map((author, index) => <React.Fragment key={index}>{author}<br/></React.Fragment>)}</div>
          </div>
        })}
      </ol>
    </div>
  </div>);
}
