import { useState } from "react";
import "../App.css";
import { search } from '../BooksAPI';

export const SearchBook = ({ showSearchPage, setShowSearchpage, shelves, setShelves }) => {
  const [searchedBooks, setSearchedBooks] = useState([]);
  const onSearch = async (event) => {
    if (event.key === 'Enter') {
      let response = await search(event.target.value, 50);
      let _searchedBooks = response.map((res) => { 
        return { authors: res.authors, id: res.id, title: res.title, image: res.imageLinks.thumbnail } 
    });
      debugger;
    }
  }

  return (
  <div className="search-books">
    <div className="search-books-bar">
      <a
        className="close-search"
        onClick={() => setShowSearchpage(!showSearchPage)}
      >
        Close
      </a>
      <div className="search-books-input-wrapper">
        <input
          type="text"
          placeholder="Search by title, author, or ISBN"
          onKeyDown={onSearch}
        />
      </div>
    </div>
    <div className="search-books-results">
      <ol className="books-grid">
        <div className="book">
          <div className="book-top">
            <div
              className="book-cover"
              style={{
                width: 128,
                height: 193,
                backgroundImage:
                  'url("http://books.google.com/books/content?id=uu1mC6zWNTwC&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE73pGHfBNSsJG9Y8kRBpmLUft9O4BfItHioHolWNKOdLavw-SLcXADy3CPAfJ0_qMb18RmCa7Ds1cTdpM3dxAGJs8zfCfm8c6ggBIjzKT7XR5FIB53HHOhnsT7a0Cc-PpneWq9zX&source=gbs_api")',
              }}
            ></div>
            <div className="book-shelf-changer">
              <select>
                <option value="none" disabled>
                  Move to...
                </option>
                <option value="currentlyReading">
                  Currently Reading
                </option>
                <option value="wantToRead">Want to Read</option>
                <option value="read">Read</option>
                <option value="none">None</option>
              </select>
            </div>
          </div>
          <div className="book-title">1776</div>
          <div className="book-authors">David McCullough</div>
        </div>
      </ol>
    </div>
  </div>);
}
